<?php	session_start();	include "db_conn.php";	$usuario = $_POST['usuario'];	$passwd = $_POST['passwd'];		if (empty($usuario)) {		header("Location: iniciosesion.php?error=Se requiere un nombre de usuario");		exit();	}else if(empty($passwd)){		header("Location: iniciosesion.php?error=La contraseña es obligatoria");		exit();	}else{		$sql = "SELECT * FROM login_users WHERE usuario='$usuario' AND passwd='$passwd'";		$result = mysqli_query($connection, $sql);		if (mysqli_num_rows($result) === 1) {			$row = mysqli_fetch_assoc($result);			if ($row['usuario'] === $usuario && $row['passwd'] === $passwd) {				$_SESSION['id'] = $row['id'];				$_SESSION['usuario'] = $row['usuario'];				#$_SESSION['departamento'] = $row['departamento'];				header("location: homeAdmin.php");			}else{				header("Location: iniciosesion.php?error=Nombre de usuario o contrasena incorrectos");				exit();			}		}else{			header("Location: iniciosesion.php?error=Nombre de usuario o contraseña incorrectos");			exit();		}	}	?>