<!DOCTYPE html>
<html>    <head>		<title></title>		<link rel="stylesheet" type="text/css" href="css/styleind.css">	</head>    <body>			<?php			$user= "root";			$pass = "";			$host= "localhost";			$db_nombre = "velez";			$connection = @mysqli_connect($host, $user, $pass, $db_nombre);
			if (!$connection) {				echo "Connection failed!";			}else{				$nombre = $_REQUEST['nombre'];				$primerApellido = $_REQUEST['primer_apellido'];				$segundoApellido = $_REQUEST['segundo_apellido'];				$sexo = $_REQUEST['sexo'];				$dni = $_REQUEST['dni'];				$nacionalidad = $_REQUEST['nacionalidad'];				$situacion = $_REQUEST['situacion'];				$fecha_nacimiento = $_REQUEST['fecha_nacimiento'];				$localidad = $_REQUEST['localidad'];				$provincia = $_REQUEST['provincia'];				$codigo_postal = $_REQUEST['codigo_postal'];				$direccion = $_REQUEST['direccion'];				$correo_electronico = $_REQUEST['correo_electronico'];				$grado = $_REQUEST['grado'];				$ciclo = $_REQUEST['ciclo'];				$curso = $_REQUEST['curso'];				$jornada = $_REQUEST['jornada'];
				$insercion = "INSERT INTO matriculas (nombre,primer_apellido,segundo_apellido,sexo,dni,nacionalidad,situacion,fecha_nacimiento,localidad,provincia,codigo_postal,direccion,correo_electronico,grado,ciclo,curso,jornada) VALUES ('$nombre','$primerApellido','$segundoApellido','$sexo','$dni','$nacionalidad','$situacion','$fecha_nacimiento','$localidad','$provincia','$codigo_postal','$direccion','$correo_electronico','$grado','$ciclo','$curso','$jornada')";
				$registro = mysqli_query($connection,$insercion);						if($registro){				echo "Haga clic en el siguiente botón para volver atrás";				echo "<p><a href=\"index.html\" class=\"botonazul2\">Usuario registrado con éxito</a></p>";			}else{				echo "Haga clic en el siguiente botón para volver atrás";				echo "<p><button class=\"botonazul2\" onclick=\"history.back()\">No se ha podido registrar</button></p>";			}		}				mysqli_close($connection); //Cerrar la conexión a la base de datos		?>	</body></html>